sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    if (g_form.getValue('u_location') != '')
        return;
    var city = g_scratchpad.city;
    var country = g_scratchpad.country;

    if (city && country) {
        g_form.setValue('u_location', city + ', ' + country);
    } else if (city) {
        g_form.setValue('u_location', city);
    } else if (country) {
        g_form.setValue('u_location', country);
    }
    if (city || country) {
        g_form.showFieldMsg('u_location', 'Value set automatically - you may override by editing', 'info');
    }
}
